import "./style.css";
import Button from "../Button";


const defaultList = [
    { title: "ItemOne" },
    { title: "ItemTwo" },
    { title: "ItemThree" }
];

const [list, updateList] = useState(defaultList);

const handleRemoveItem = (e) => {
    const name = e.target.getAttribute("title")
    updateList(list.filter(item => item.title !== name));
};

function ListIem(props) {
    return ( <
        div className = { "flex item" } >
        <
        p className = { "title" } > { props.title } < /p> <
        Button onClick = { handleRemoveItem } > Delete < /Button> < /
        div >
    );
}

export default ListIem;
